// This is my first "moving" shader and my second shader

#define sat(a) clamp(a, 0., 1.)
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // thanks to z0rg and FabriceNeyret2
    vec2 uv = ( fragCoord -.5*iResolution.xy ) /iResolution.y;;

    vec3 col = vec3(.6, .7, 0.);

    vec2 circle  = vec2(sin(iTime) * .3, sin(iTime) * .3);

    float center = distance(circle, uv);

    float shape = 1. - sat((center - .2) * 100.);

    vec3 image = col * shape;

    // Output to screen
    fragColor = vec4(image,1.0);
}
