void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    uv = uv * 20.0;

    uv.y += sin(uv.x*0.5);
    uv.x += cos(uv.y * 0.2) * 10.0;
    float verticalWobble = (sin(uv.y + iTime) + cos(uv.y*1.5 + iTime)*0.5)/ 8.0  ;
    float horizontalWobble = (cos(uv.x + sin(uv.y)) + sin(uv.y*3.0 + iTime) + 1.0) / 2.0;
    float t = horizontalWobble + verticalWobble;
    t = smoothstep(0.4,0.6,t);

    // Time varying pixel color
    vec3 colA = vec3(1.0,0.96,0.76);
    vec3 colB = vec3(0.65,1.0,0.85);
    vec3 col = mix(colA, colB, t);

    // Output to screen
    fragColor = vec4(col,1.0);
}
