void mainImage( out vec4 O, in vec2 F ) {
    vec2 C = iResolution.xy,
         o = (F+F-C)/C.y/0.85;
    float d = iTime*0.5, e;

    for (O *= e; e < 2e2; e += .5)
        O += .0005/abs(length(o + vec2(cos(e/4.0 + d), 
        sin(e*0.45 + d )) * sin(d*0.9+e*0.35)) - sin(e+d*.25)/6.0 - .01)
        * (1. + cos(e*.7+d+length(o*2. +d)*6. +vec4(0,3,4,0)));



}
