//PARAMETERS. These can be anything you want, just adjust them until the noise looks random enough
const float variant1 = 56.2327467238;
const float variant2 = 4029.9435394853;
const float scale = 1.;

float RNG(vec2 UV)
{
    //Basic RNG, copied from the approach by The Art of Code (Martijn Steinrucken)
    return fract(sin(UV.x * variant1 + UV.y * variant2) * 5647.0);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    // Time varying pixel color
    vec3 col = vec3(1.);

    //https://www.youtube.com/watch?v=zXsWftRdsvU&list=PLGmrMu-IwbguU_nY2egTFmlg691DN7uE5&index=18
    float c = RNG(uv*scale);

    // Output to screen
    fragColor = vec4(c,c,c, 1.0);
}

