vec2 triangle_wave(vec2 a){
    vec2 a2 =
        vec2(1.,.5)
    ,
    a1 = a+a2;
    return abs(fract((a1)*(a2.x+a2.y))-.5);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec3 col = vec3(0.);
    float t1 = 8.;
    vec2 uv = (fragCoord)/iResolution.y/t1/2.0;
    uv.x += iTime/t1/12.0;
    if(iMouse.z>.5)
    uv = uv.xy + iMouse.xy / iResolution.xy/t1;
    float scale = 1.5;
    vec2 t2 = vec2(0.);
    for(int k = 0; k < 12; k++){
        vec2 uv1 = uv;
        uv = (uv+t2)/scale;
        t2 = triangle_wave(uv-.5);
        uv = t2-triangle_wave(uv.yx);
        col.x =
            max(max((t2.y-t2.x*sign(uv.x)),abs(uv.y-uv.x))/3.,col.x)
        ;
        col =
            max(abs(col-1.+col.x),col/4.);
            //max((col-1.+col.x/1.5),(1.-col-col.yzx));
    }
    fragColor = vec4(col,1.0);
}

