vec2 mainSound(int samp, float time){
 time *= 4.;
 float s1 =
      4.;

  float
  m4 =
      pow(2.,-2.+sqmod(floor(time/s1),3.))
  ,
  t=
      time/(1.+m4)
  ,
  m1 =
      sqmod(floor(t*s1)/s1,s1)
  ,
  m3 =
      sqmod(floor(t*s1*2.)+t*(.5-m4)*2.,s1-m1);
  t *=
      s1*s1/(1.+m4)
  ;
  float m2 =
      1. + sqmod(t/s1,s1)
  ;

  float a=sqrt((1.-sqrt(max(fract(t/s1/s1),fract(t/s1)))))*.2/(1.+m4),

  nb = pow(2.,(m3+m2)/5.+6.5)*4./(1.+mod(floor(t),s1))/2.;

  return vec2(fract(time*nb*.998/2.)*a
   ,fract(time*nb)*a);

}
