#define PI 3.14159
#define TAU 2.*PI
#define T iTime*2.


float circle (vec2 p, float r){
    return length(p)-r;
    }

float sdOnion(in float Object, float r){
    return abs(Object)-r;
}

vec3 pal( in float t, in vec3 a, in vec3 b, in vec3 c, in vec3 d )
{
    return a + b*cos( 6.28318*(c*t+d) );
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    vec2 nuv = (fragCoord-.5*iResolution.xy)/iResolution.y;

    vec2 disuv = nuv;
    vec3 col = vec3(0.);
    float change = (sin(T*.25)*.2)+.205;

    for(int i = 0;i<16;i++){
        float id = float(i);
        disuv = nuv+vec2(sin(id/16.*TAU),cos(id/16.*TAU))*.2;

        float test = smoothstep(1./iResolution.y,0.,circle(disuv+(vec2(sin(T+(id*10.)),cos(T+(+id*10.))))*.2,change));
        float testOn = smoothstep(1./iResolution.y,0.,sdOnion(circle(disuv,.2),.001));


    test *= testOn;
    col += test*pal(iTime*2.+id*(length(disuv*20.)), vec3(.5,.5+(id/16.)*length(disuv),.0+(id/16.)),vec3(.2,.2,.1),vec3(.8,.5,.2),vec3(.99));
    }
    uv -= .5;
    uv *= 1.2;
    uv += .5;
    vec3 tex = texture(iChannel0,uv).rgb;
    col += (tex*.6)*cos(vec3(.2,.1,.4)*T*TAU);
    // Output to screen
    fragColor = vec4(col,1.0);
}
