//https://iquilezles.org/articles/spherefunctions/
float sphIntersect( vec3 ro, vec3 rd, vec4 sph )
{
    vec3 oc = ro - sph.xyz;
    float b = dot( oc, rd );
    float c = dot( oc, oc ) - sph.w*sph.w;
    float h = b*b - c;
    if( h<0.0 ) return -1.0;
    h = sqrt( h );
    return -b - h;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    const float FOV = 60.0;

    vec2 uv = fragCoord / iResolution.xy;

    float halfFOV = FOV * 0.5;

    float minPosition = tan(radians(-halfFOV));
    float maxPosition = tan(radians(halfFOV));

    float percentage = iResolution.y / iResolution.x;
    float difference = 1.0 - percentage;

    float x = mix(minPosition, maxPosition, uv.x);
    float y = mix(minPosition, maxPosition, (uv.y * percentage) + (difference * 0.5));

    vec3 rayOrigin = vec3(0, 0, -1);
    vec3 rayDirection = normalize(vec3(x, y, 1));

    int sphereCount = 32;
    float space = 1.61803398875;
    float minDistance = -1.0;
    vec3 color = vec3(0);
    for(int i = 0; i < sphereCount; i++)
    {
        float time = iTime + float(i) * space;
        vec4 sphere = vec4(sin(time * 1.75), cos(time * 1.75), 5.0 + tan(time), 0.075);
        float sphereDistance = sphIntersect(rayOrigin, rayDirection, sphere);

        if(sphereDistance >= 0.0 && (minDistance < 0.0 || sphereDistance < minDistance))
        {
           color = sphere.xyz * exp(-sphereDistance * 0.4);
           minDistance = sphereDistance;
        }
    }

    if(minDistance < 0.0) color.rgb = vec3(0);

    fragColor.rgb = color;
}
