// Hash without Sine
// MIT License...
// Copyright (c)2014 David Hoskins.
//https://www.shadertoy.com/view/4djSRW
float hash12(vec2 p){
	vec3 p3  = fract(vec3(p.xyx) * .1031);
    p3 += dot(p3, p3.yzx + 33.33);
    return fract((p3.x + p3.y) * p3.z);
}

float sigmoidHash12(vec2 seed, float scale) {
    float r = hash12(seed);
    return 1./(1.+exp((r-.5)*scale*2.));
}

bool inRange(float v, float min, float max) {
    return v > min && v < max;
}

#define loop(i,itr) for(int i=0;i<itr;i++)
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    vec3 c = vec3(0);
    float pix = 1./iResolution.x;

    loop(i,50)
    loop(j,50){

        vec2 off = vec2(i,j)+iTime;

        if( inRange( hash12 (fragCoord.xx+off), uv.x-pix*.5, uv.x+pix*.5))
            c.r++;

        if( inRange( sigmoidHash12 (fragCoord.xx+off, 5.+(sin(iTime*5.)*.5+.5)*50.), uv.x-pix*.5, uv.x+pix*.5))
            c.g++;
    }

    fragColor = .25+vec4(c.r*10.>fragCoord.y,
                     c.g*10.>fragCoord.y,
                     0,1)*.75;
}
