void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    float mv1 = (sin(iTime-uv.y*3.0)+3.0)/10.0;
    float mv2 = (sin(iTime-uv.y*3.0)+6.5)/10.0;
    float wv1 = (sin(iTime-uv.y*3.0))/2.0;
    float wv2 = (sin(iTime-uv.y*10.0))/2.0;

    if(mv1 > uv.x){
                fragColor = vec4(0,1,0,1)*(wv1+1.0)/2.4;
        } else if(mv2 > uv.x){
                fragColor = vec4(1,1,1,1)*(wv1+0.6)/0.65;
        } else if(mv2 < uv.x){
                fragColor = vec4(1,0,0,0)*(wv1+0.6)/0.65;
        }

}
