void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 st = fragCoord/iResolution.xy;

    vec2 fSt = st* 20.;

    //perlin noise
    float p = 0.;
    p += cos(fSt.x * 3.45 + iTime * 2.4);
    p += cos(fSt.x * 1.45 + iTime * .4);
    p += cos(fSt.x * .45 + iTime * -.2);
    p*=0.168;

    //parameters
    float velocity = 1.;

    float circle = length(fract(fSt)-0.5);
    float effect = step(circle, sin(fSt.y+fSt.x-iTime * velocity)*p);
    effect += step(effect, sin(fSt.y+fSt.x-(iTime-3.260) * velocity)*p);

    //effect = step(circle, sin(st.y*st.x-iTime * velocity)*p);
    //effect += step(effect, sin(st.y*st.x-(iTime-3.260) * velocity)*p);

   vec3 color = vec3(effect)*vec3(0.825,5.000,0.390);
   color = mix(color, vec3(0.), step(0.5, length(st-0.5)));

    fragColor = vec4(color,1.0);
}
