void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;

    vec2 cuv = uv * 2.0f - 1.0f;

    float bn = textureLod(iChannel0, (fragCoord + vec2(0, -iTime*10.0f))/1024.0f, 0.0f).r * 2.0f - 1.0f;
    float rr = textureLod(iChannel0, vec2(iTime*1.0f/1024.0f), 0.0f).r;

    vec3 color = vec3(0);

    cuv *= 14.0f;

    float v1 = 0.0f;

    const float lim = 40.0f;
    for (float i = 0.0f ; i < lim ; ++i) {
        /////////////////////////////////the star
        float v =0.0;
        float lv = smoothstep(0.0,1.0,1.0f - exp(-iTime));  
        v = 0.1f*lv/length(cuv);

        v1 += 0.1f/abs(dot(cuv,vec2(0,1)));

        v *= pow(v1,0.2f+0.1f*lv);

        color += v;

        cuv.y += (v1-v)*i/(lv);

        cuv.x += v1*cos(cuv.y+iTime)*(iTime);
        cuv.y += 16.0f*v*sin(cuv.x+lv*iTime);
    }

    color = vec3(0.05f*(color.x),0.01f,(v1*v1)*1.01f) + vec3(0.99,0.99,1)*color;
    fragColor = vec4(color + bn * (7.0f/255.0f),1.0);
}
