vec2 complexSquare(vec2 balloon)
{
    return vec2(balloon.x * balloon.x - balloon.y * balloon.y,
        2.0 * balloon.x * balloon.y);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Don't worry about this
    vec2 point = (2.0 * fragCoord - iResolution.xy) / iResolution.y;

    vec2 C = point;
    vec2 z = vec2(0.0);
    int iterations;

    // Do this 100 times
    for (iterations = 0; iterations < 100; iterations += 1)
    {
        z = complexSquare(z) + C;
        if (length(z) > 2.0)
        {
            break;
        }
    }
    float intensity = float(iterations) / 50.0;
    if (iterations == 100)
    {
        intensity = 0.0;
    }

    fragColor = vec4(intensity);
}
