void DrawSquare(in vec2 uv,in vec3 color,in vec2 pos,in vec2 size,out vec4 outcolor){
    vec2 posn = -pos/iResolution.xy;
    if(uv.x+posn.x>0.0 && uv.x+posn.x<size.x &&
    uv.y+posn.y>0.0 && uv.y+posn.y<size.y)
    {
         outcolor=vec4(color,1);
    } 
}

void DrawCircle( in vec2 uv,in vec3 color, in vec2 pos, in float radius,out vec4 outcolor){
    vec2 posn = pos/iResolution.xy;  
    float distanceToCenter = distance(uv, posn);
    if(radius>distanceToCenter){
       outcolor=vec4(color,1);
    }
}

void car(in vec2 pos,in vec2 uv,out vec4 fragColor){
 DrawSquare(uv,vec3(0.5,1,0.5),pos+vec2(1,0),vec2(0.1,0.1),fragColor);
 DrawSquare(uv,vec3(0.5,1,0.5),pos+vec2(1,0),vec2(0.2,0.07),fragColor);
 DrawCircle(uv,vec3(1,1,1),pos,0.05,fragColor);
 DrawCircle(uv,vec3(1,1,1),pos+vec2(100,0),0.05,fragColor);

}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    float yOffset = abs( 5.0 * fract(iTime) - 0.5) * 0.5;
    vec2 uv = fragCoord/iResolution.xy;
    fragColor=vec4(0,0,0,0);
    if(uv.y>0.10)
    {
    fragColor=vec4(0,sin(uv.y),0.7,0.9);
    }

     car(vec2(100,25.0+yOffset),uv,fragColor);
}
