vec2 fixUV(vec2 c) {
    return 3.0 * (2.0 * c - iResolution.xy) / min(iResolution.x, iResolution.y);
}
vec3 panleGrid(vec2 uv) {
    vec3 col = vec3(0.0);
    vec2 cell = 1.0 - (2.0 * abs(fract(uv) -0.5));
    if(cell.x < 2.0 * fwidth(uv.x)) {
        col = vec3(1.0);
    }
    if(cell.y < 2.0 * fwidth(uv.y)) {
        col = vec3(1.0);
    }
    if(abs(uv.x) <= fwidth(uv.x)) {
        col = vec3(1.0,0,0);
    }
    if(abs(uv.y) <= fwidth(uv.y)) {
        col = vec3(0,1.0,0);
    }
    return col;
}
void mainImage(out vec4 fragColor, in vec2 fragCrood) {
    vec2 uv = fixUV(fragCrood);
    fragColor = vec4(panleGrid(uv), 1.0);
}
