bool circle(vec2 uvCenter, float diameter, vec2 uvCoord) {
    return distance(uvCenter * iResolution.xy, uvCoord * iResolution.xy) 
           < diameter * iResolution.y / 2.0;
}

bool rectangle(vec2 uvCenter, vec2 shape, vec2 uvCoord) {
    return abs((uvCenter.x * iResolution.x) - (uvCoord.x * iResolution.x)) 
           < (shape.x * iResolution.y / 2.0)
           &&
           abs((uvCenter.y * iResolution.y) - (uvCoord.y * iResolution.y))
           < (shape.y * iResolution.y / 2.0);
}

bool caminhao(vec2 uvCenter, vec2 uvCoord)
{
   return
   rectangle(uvCenter + vec2(0.0, 0.0), vec2(0.7, 0.3), uvCoord) ||
   rectangle(uvCenter + vec2(0.25, -0.04), vec2(0.25, 0.25), uvCoord) ||
   circle(uvCenter + vec2(-0.1, -0.18), 0.15, uvCoord) ||
   circle(uvCenter + vec2(0.22, -0.18), 0.15, uvCoord);
}

