{
    float yOffset = abs( 5.0 * fract(iTime) - 1.0) * 1.0;
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uvCoord = fragCoord/iResolution.xy;

    fragColor=vec4(.9,.6,.7, 1.0);

    //drawCircle(uv, vec3(1.5,.5,1.5), iMouse.xy, 0.05,fragColor);

    //drawSquare(uv,vec3(1.5,.5,1.5),iMouse.xy, vec2(0.1, 0.2), fragColor);
    //DrawAPole(vec2(15,0),vec2(uv.x +2.0 *fract(iTime)-1.0, uv.y), fragColor);
    if (caminhao(vec2(0.3,0.5)+ vec2(sin(iTime * 0.5), 0.0) / 5.0,uvCoord)) fragColor = vec4(0.0, 0.0, 0.8, 1.0);
