// Add pixels with the mouse

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    vec3 col = vec3(texture(iChannel0, uv).r);
    fragColor = vec4(col,1.0);
}

int Neighbours(ivec2 position)
{
    int res = 0;

    for (int y = -1; y <= 1; y++)
    {
        for (int x = -1; x <= 1; x++)
        {
            if (x == 0 && y == 0) continue;
            res += texelFetch(iChannel0, position + ivec2(x, y), 0).r > 0. ? 1 : 0;
        }
    }

    return res;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;

    vec2 dist = (iMouse.xy-fragCoord.xy)/iResolution.x;
    float threshold = 1./(iResolution.x*.125);
    float click = 0.;
    if (length(dist) < threshold) 
    {
        click = 1.;
    }

    int n = Neighbours(ivec2(fragCoord));
    bool alive = texelFetch(iChannel0, ivec2(fragCoord), 0).r > 0.;
    alive = (alive && (n == 2 || n == 3)) || (!alive && n == 3);  

    fragColor = vec4(step(0.5, click + (alive ? 1. : 0.)),0.0,0.0,1.0);
}
