void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    //Two gradient color schemes
    vec3 col1 = 0.5 + 0.5*cos(iTime+uv.xyx+vec3(0,2,4));
    vec3 col2 = 0.5 + 0.5*cos(iTime+uv.yxy+vec3(2,4,6));

    //Read the rgb value from the current coordinate on the video
    vec3 texCol = texture(iChannel0, uv).rgb;
    //First test is to show the original video
    vec3 col = texCol; 

    //The background is RGB = (13, 163, 37)
    //This is roughly: (0.051, 0.637, 0.145)
    //Subtract the background color
    vec3 background = texCol - vec3(0.051, 0.637, 0.145);
    //Show the "darkened", subtracted video with black background
    col = background;

    //Create a variable that will be 1 inside the character and 0 outside
    //with a smooth transition outline
    float live = smoothstep(0.1, 0.2, length(background));

    //Just a test with a sharp outline
    //live = step(0.1, length(background));

    //Use col1 for the character and col2 for the background
    col = col1*live + col2*(1.0-live);
    // Output to screen
    fragColor = vec4(col,1.0);
}
