float sdSegment(vec2 p, vec2 a, vec2 b)
{
    vec2 pa = p - a, ba = b - a;

    float h = clamp(dot(pa,ba) / dot(ba, ba), 0.0, 1.0);

    return length(pa - ba * h);
}

// [-0.5, 0.5]
float aa_3(float d) {
    return clamp(0.5 - d, 0.0, 1.0);
}

// [-1, 1]
float aa_4(float d) {
    return clamp(0.5 - 0.5 * d, 0.0, 1.0);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec3 fg = vec3(0.0, 0.0, 0.0);
    vec3 bg = vec3(1.0, 1.0, 1.0);

    float t = 0.3 * iTime;
    vec2 p1 = vec2 (300.5, 250.5);
    vec2 p2 = p1 + 200.0 * vec2 (cos(t), sin(t));

    // 20条 相隔很近的线段（相差 2像素）
    for (int i = 0; i < 20; i += 1) {
        p1 += vec2(2.0);
        p2 += vec2(2.0);

        float d = sdSegment(fragCoord, p1, p2);
        float a = aa_3(d);
        bg = mix(bg, fg, a);
    }

    fragColor = vec4(bg, 1.0);
}
