#define k(b, c) texture(iChannel0, vec2(b, c)).r
void mainImage( out vec4 r, in vec2 d )
{
    vec2 p = d/iResolution.y, m = iMouse.xy/iResolution.y;
    r = vec4(0);
    if(k(.032, 0.5) <= 0.)
        if(distance(p, m) > .02 || iMouse.z < 0.) discard; else r = vec4(1.-k(0.063, 1));
}
