void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;

    vec4 imagePixel = texture(iChannel0,uv);
    // Output to screen
    float luminance =0.2126*imagePixel.r+0.7152*imagePixel.g+0.0722*imagePixel.b;

    fragColor = vec4(luminance);
}
