const float PI=3.1415926535;
vec2 rot(vec2 a,float theata){
    return a.xx*vec2(cos(theata),sin(theata))+a.yy*vec2(-sin(theata),cos(theata));
}
vec2 cMul(vec2 a, vec2 b) {
	return vec2(a.x*b.x-a.y*b.y,a.x*b.y+a.y*b.x);
}
vec2 cLog(vec2 a) {
	float b =  atan(a.y,a.x);
	if (b<0.0) b+=2.0*3.1415926535;
	return vec2(log(length(a)),b);
}
vec2 cExp(vec2 z) {
	return exp(z.x) * vec2(cos(z.y), sin(z.y));
}
vec2 cPow(vec2 z, vec2 a) {
	return cExp(cMul(cLog(z), a));
}
vec2 cDiv(vec2 a, vec2 b){
  float d = dot(b,b);
  return vec2(dot(a,b),a.y*b.x-a.x*b.y)/d;
}
vec2 rotClamp(vec2 pos,int n){
    float alpha=-atan(pos.x,pos.y);
    float tmp=PI/float(n);
    return abs(rot(pos,-alpha+mod(alpha,2.*tmp)-tmp));
}

vec2 f( vec2 z, vec2 c )
{
	return abs(cPow(z,vec2(3,0)))-exp(1.)*abs(z)+c;
}

vec2 df( vec2 z, vec2 c )
{
	vec2 e = vec2(0.001,0.0);
    return cDiv( f(z,c) - f(z+e,c), e );
}

vec4 color(vec2 pos){
    vec2 pos0=pos;

    const int S=200;
    pos=rot(pos,-PI/6.);
    pos=rotClamp(pos,6);
    pos=rot(pos,-PI/4.);    
    vec2 z1=pos;
    vec2 p=abs(pos+vec2(0.25,-1.5));

    vec2 dz = vec2( 1.0, 0.0 );

    for(int i=0;i<=S;i++){
		if( dot(z1,z1)>100000.0 ) break;

        // chain rule for derivative		
		dz = cMul( dz, df( z1, p ) );

        // function		
		z1 = f( z1, p );

    }
	float h = 0.5*log(dot(z1,z1))*sqrt( dot(z1,z1)/dot(dz,dz));

    vec3 color=vec3(1.)*abs(1.-h*50000.)*0.0125;

    return vec4(color,1);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = 2.*fragCoord/iResolution.xy-1.;

    uv.x*=iResolution.x/iResolution.y;

    // Output to screen
    fragColor = color(2.*uv);
}
