void mainImage(out vec4 O, vec2 I)
{
    //Resolution for scaling
    vec2 r = iResolution.xy, o;
    //Clear fragcolor
    O*=0.;
    //Render 50 lightbars
    for(float i=fract(-iTime); i<25.; i+=.5)
        //Offset coordinates (center of bar)
        o = (I+I-r)/r.y*i + cos(i*vec2(1.5,.1)+iTime / 2.),
        //Light color
        O += vec4(1,1,2,0)/i / max(i*i,10.)*.1 / (i/5e2+
        //Attenuation using distance to line
        length(o-vec2(clamp(o.x,-4.,4.),i+o*sin(i)*.01-5.))/i);
}
