float Circle(vec2 uv,vec2 position, float blur, float size){
    float d = length(uv - position);
    float c = smoothstep(size, size-blur, d);
    return c;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;

    uv = uv*2.-1.;

    uv.x *= iResolution.x/iResolution.y;

    float timing = sin(iTime * 2.);

    float smile = 1.;
    smile -= Circle(vec2(abs(uv.x), uv.y), vec2(.35, 0.2), .01, .21) * (1.- Circle(vec2(abs(uv.x), uv.y), vec2(.35, timing * .05-.35), .01, .4));
    smile -= Circle(vec2(abs(uv.x), uv.y), vec2(.55, -0.1), .2, .21);
    smile -= (1.-Circle(uv,vec2(.0,.4), .01, .6)) * Circle(uv,vec2(.0, timing * -.15), .01, .38);
    smile *= Circle(uv, vec2(.0), .01, .8);


    float highlight = 0.;
    highlight += Circle(vec2(abs(uv.x-.05), uv.y-.05), vec2(.35, 0.2), .01, .07);
    highlight += Circle(vec2(abs(uv.x+.07), uv.y+.05), vec2(.35, 0.2), .01, .05);
    highlight += Circle(vec2(abs(uv.x-.08), uv.y+.08), vec2(.35, 0.2), .01, .03);

    float pink = 0.;
    pink += (1.-Circle(uv,vec2(.0,.4), .01, .6)) * Circle(uv,vec2(.0, timing * -.15), .01, .38);
    pink += Circle(vec2(abs(uv.x), uv.y), vec2(.55, -0.1), .2, .21) * 2.;


    vec3 col =  (vec3(1.) * highlight) + (vec3(1.,.9,.1)*smile) + (vec3(0.8,0.2,0.2) * pink);

    fragColor = vec4(col,1.0);
}
