void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    fragColor = texture(iChannel0, uv);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec3 old = texture(iChannel1, fragCoord/iResolution.xy, 3.).rgb*0.999;
    vec3 color = texture(iChannel0, fragCoord/iResolution.xy).rgb;
    float a = clamp(max(0., color.g-color.r-color.b)*100., 0., 1.);
    fragColor = vec4(mix(old, color, 1.-a), 1.);
    //fragColor = vec4(a);
}
