void mainImage (out vec4 O, vec2 u) {
    vec2 r = iResolution.xy; u += u - r;
    float i = 0.;
    for (O *= i; i++ < 1e2; O += pow(.005/              // attenuation
        length(u/r.y + i*(sin(iTime)*.5+.5)*.007 - .5) // position
        *(sin(.1*i+vec4(1,2,3,0))+1.), O-O+1.3)        // color
    ) u *= mat2(sin(vec4(0,33,11,0) +                  // rotation
           (i < 2. ? iTime : sin(iTime/1.)*.1+.2) ));
}
