float circleSDF(vec2 st) {
    return length(st);
}

float fill(float x, float size){
    return 1.-step(size, x);
}

float stroke(float x, float s, float w) {
    float d = step(s, x+w*.5)- step (s, x-w*.5);
    return clamp(d, 0., 1.);
}

vec2 mov (vec2 st,float x) {
    vec2 uvm = vec2 (st - vec2(sin(iTime*10.+x)*.1,0.));
    return uvm;
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    vec2 uv = 2. * fragCoord/iResolution.xy -1.;
    uv.x *= iResolution.x/iResolution.y;


    float cc = circleSDF(mov(uv,0.));
    float cc2 = circleSDF(mov(uv - vec2(0.,.1),-1.));
    float cc3 = circleSDF(mov(uv - vec2(0.,.2),-2.));
    float cc4 = circleSDF(mov(uv - vec2(0.,.3),-3.));
    float cc5 = circleSDF(mov(uv - vec2(0.,.4),-4.));
    float cc6 = circleSDF(mov(uv - vec2(0.,.47),-5.));

    vec3 col2 = vec3(0,.5,.4);
    vec3 c = vec3(0.0);
    c += fill(cc6,.05)* (col2 -.3);
    c += fill(cc5,.10)* (col2 -.3);
    c += fill(cc4,.15)* (col2 -.3);
    c += fill(cc3,.20)* (col2 -.3);
    c += fill(cc2,.25)* (col2 -.3);
    c += fill(cc,.3)* (col2 -.3);


    float p_angle = atan(uv.x, uv.y);
    float p_distance = -length(uv) ;
    vec2 tst = vec2(p_distance, p_angle);
    vec3 tcol = texture (iChannel0, vec2(1.0 / tst.x + iTime, tst.y)).xyz;
    tcol *= min(tst.x*.1, 1.0);




    vec2 uv3 = fragCoord/iResolution.xy;
    vec3 col = 0.5 + 0.5*cos(uv3.xyx+vec3(0,5,4)) + c + tcol;

    //col = vec3(c);


    fragColor = vec4(col,1.0);
}
