void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    vec2 ratio = vec2(iResolution.x / iResolution.y, 1.0);
    uv *= ratio;
    uv.x -= 0.4;


    vec2 centered = uv - vec2(0.5, 0.5);
    float angle = atan(centered.x, centered.y);
    vec2 coords = vec2(length(centered) - iTime * 0.1, angle / 6.28 + vec2(0.1) * iTime);


    float noise = texture(iChannel1, coords).x;
    float edge = 1.0 - smoothstep(0.2, 0.3 + noise * 0.25, length(centered));

    vec3 color = texture(iChannel0, coords * 2.0).rgb * edge;


    fragColor = vec4(color, 1.0);
}
