void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xx;
    float offset = texture(iChannel0, uv * 200.5 + vec2(iTime * 0.5 + sin(iTime) * .2, 0)).x;
    uv *= 15.;
    uv += vec2(offset);
    vec3 col = vec3(float(length(uv - round(uv)) < 0.3));
    fragColor = vec4(col,1.0);
}
