#define t iTime
#define r iResolution.xy

void mainImage( out vec4 fragColor, in vec2 fragCoord ){
	vec3 c;
	float l,z=t;
    vec2 p=fragCoord.xy/r;
    p-=.5;
    l=length(p);
    l*= 10.0;
    z *= 0.5;
    l-=z;
    c[2] = fract(l);
    float pst = step(0.9, c[2]);
    c = c*(1.0-pst) + pst*vec3(1.0, 1.0, 1.0);
	fragColor=vec4(c,1.0);
}
