#define pure_t iTime
#define sawtooth_t fract(iTime)
#define triangular_t (abs(fract(iTime) - 0.5) * 2.0)
#define PI 3.1415926535
#define PI2 (2.*3.1415926535)




void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy * 2. - 1.;
    uv.y *= iResolution.y/iResolution.x;


    float timer = fract(pure_t / 2.0);
    float signal_x = pow(smoothstep(0.0, 1.0, timer), 4.);
    signal_x += pure_t / 8.;

    float angle = atan(uv.y, uv.x);
    float r = cos(angle), i = sin(angle);
    float rr = cos(signal_x * PI2), ri = sin(signal_x * PI2);
    float diff = sqrt(pow(rr - r, 2.) + pow(ri - i, 2.)); 



    vec3 col = vec3((rr/2.0+0.5)/3.0, ri/2.+0.5 ,1.0);

    float distance = abs(length(uv) - 0.4) * 2.;

    col *= 0.15 / (0.15 + distance);
    col *= 0.15 / (diff + 0.15);





    // Output to screen
    fragColor = vec4(col,1.0);
}
