void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
  vec2 uv = fragCoord/iResolution.xy; // <0,1>

  vec3 col = vec3(1.0, 0.0, 0.5); // start with black

  col = vec3(step(0.5, sin(uv*iTime)), abs(sin(iTime))); // perform step function across the x-component and y-component of uv

  float d = length(uv) * exp(-length(uv));
  d = smoothstep(1.0, 0.5, 0.2);
  // Output to screen
  fragColor = vec4(d, d, d, 1.0);
}
