// Texture sampling function with scaling and intensity adjustment
#define INTENSITY  1.
#define COUNT 100.
#define SampleTexture(U) texture(iChannel0, 2.0 * U) * INTENSITY

// Texture for the mean color (you can use different textures or predefined colors)
#define MeanTexture texture(iChannel0, 2.0 * U, 1.0) * INTENSITY

// #iChannel0 "file://angelTrumpetOne1.png"
// Variant texture sampling options:
// #define MeanTexture vec4(65, 54, 46, 1) / 255.0      // Mean for Britney video
// #define SampleTexture(U) vec4(0.5 + 0.5 * sin(120.0 * U.x))   // Gabor noise. mean = 0.5
// #define SampleTexture(U) SmoothBlobKernel(U)         // Simple blob. mean = 0.5
// #define MeanTexture 0.5

// Background image texture (change the URL to use a different image)


// Smooth kernel for blending particles
#define SmoothBlobKernel(U) smoothstep(0.2, 0.0, length(U))

// Function to generate a random value based on the input seed
#define Random(seed) fract(1e4 * sin(seed + vec2(0, 73.17)))

void mainImage(out vec4 fragColor, in vec2 U)
{
    // Initialize output color
    fragColor -= fragColor;

    // Get the resolution of the image
    vec2 resolution = iResolution.xy;

    // Calculate the aspect ratio
    vec2 aspectRatio = resolution / resolution.y;

    // Check if the current fragment is close to the center

    // Normalize the coordinates
    vec2 uv = U / resolution.y;

    // Variables to hold the sum and sum of squares
    float sum = 0.0;
    float sumSquared = 0.0;

    for (float i = 0.0; i < COUNT; i++)
    {
        // Get a random value based on the iteration index
        vec2 randomOffset = Random(i);

        // Calculate the sprite position
        float xOff = Random(i).x;
        vec2 spritePosition = uv * xOff - randomOffset * aspectRatio + 0.1 * cos(i + iTime + vec2(0, 1.6)); 
        spritePosition.x += xOff*.5;
        float yOff = sin(iTime+i);
        yOff = max(0., yOff);
        spritePosition.y += yOff;


        // Calculate the kernel value
        float kernelValue = SmoothBlobKernel(spritePosition);

        // Update the sum and sum of squares
        sum += kernelValue;
        sumSquared += kernelValue * kernelValue;

        // Accumulate the color from the texture
        fragColor += kernelValue * SampleTexture(spritePosition);
    }

    // // Normalization
    // if (uv.x < aspectRatio.x / 2.0)
    // {
    //     // Linear blend
    //     fragColor /= sum;
    // }
    // else
    // {
        // Variance preserving blend
        vec4 meanColor = MeanTexture;
        fragColor = meanColor + (fragColor - sum * meanColor) / sqrt(sumSquared);
    // }
}
