float rectSDF(vec2 st, vec2 s) {
    st = st * 2.0 - 1.0;
    return max(abs(st.x / s.x), abs(st.y / s.y));
}

float fill(float x, float size) {
    return 1.0 - step(size, x);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    vec3 color = vec3(1.0);

    vec2 S = vec2(0.3, 0.8);

    vec2 offset = vec2(0.2, 0.0);

    float MX = iMouse.x / iResolution.x;
    float MY = iMouse.y / iResolution.y; 

    vec3 leftAColor = vec3(0.8, 0.9, 0.84);
    vec3 rightAColor = vec3(0.572, 0.316, 0.444);
    vec3 BColor = vec3(0.572, 0.572, 0.448);

    float ASize = 1.0;
    float BSize = 0.7;

    float rectALeft = fill(rectSDF(uv + offset, S), ASize);
    float rectARight = fill(rectSDF(uv - offset, S), ASize);
    float rectBLeft = fill(rectSDF(uv + offset, S), BSize);
    float rectBRight = fill(rectSDF(uv - offset, S), BSize);

    color = mix(mix(leftAColor, rightAColor, MX) * rectALeft, BColor * rectBLeft, rectBLeft);
    color += mix(mix(rightAColor, leftAColor, MY) * rectARight, BColor * rectBRight, rectBRight);

    fragColor = vec4(color, 1.0);
}
