renderpass.code:
float gray(vec3 c) {
    return 1./4. * dot(vec3(1, 2, 1), c);
}

vec2 surface_uv(vec2 p, float dist) {
    p /= dist + 1.;
    p.x += dist * abs(.5 + .5*cos(p.x*3.)*sin(p.y*2.));
    p.y += .1 * dist * pow(dist + 1., 2.);
    p.x += dist * sin(p.x + iTime);
    return p;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float aa = 2. / iResolution.y;
    vec2 uv = fragCoord / iResolution.xy;
    vec2 p = (fragCoord - iResolution.xy*.5) / iResolution.y;

    vec3 col = vec3(0);
    p *= iResolution.y/360.;

    float bc = gray(texture(iChannel0, p).rgb);
    col.g = bc;

    const float layers = 20.;
    const float extrusion = .002;

    for (float i = 0.; i < layers; ++i) {
        vec2 suv = surface_uv(p, i * extrusion);
        float sc = gray(texture(iChannel0, suv / 10.).rgb);

        //float fur = gray(texture(iChannel0, suv).rgb);
        float fur = gray(texture(iChannel1, suv).rgb);

        fur = smoothstep(.1, .2, fur);
        //fur = step(.1, fur);
        //col.r = fur;

        float fc = fur * sc;

        col.g = mix(col.g, fc, fur) * (.8+.2*sqrt((i+1.)/layers));
        //col.g += (fc * fur) / (i + 2.);
        //col.r = fur;
    }

    col = vec3(1, .9, .8) * sqrt(col.g)/2.;

    fragColor = vec4(col, 1.0);
}
