void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord;

uv -= vec2(0.5, 0.5)* iResolution.xy;

vec3 col = vec3( 0 ,0, 0);

float r = 300.0;

float z = sqrt(r * r - uv.r * uv.r - uv.y * uv.y);
vec3 normal = normalize (vec3 (uv.r, uv.y, z));
col = normal * 0.5 +0.5;


}

