#define TWO_PI 6.28318530718
vec3 createHsbCircle(in float, in float);

vec3 circle(vec2 uv, float proportion, vec2 mouse) {
    float radius = 0.010;
    float width = 0.003;

    // Forces the circle to maintain correct aspect ratio regardless of window aspect ratio.
    float proportionY = uv.y - mouse.y;
    float proportionX = (uv.x * proportion) - (mouse.x * proportion);
    proportionY = proportionY * proportionY;
    proportionX = proportionX * proportionX;

    float circleOutside = 1.0 - step(radius, (proportionX + proportionY));
    float circleInside = 1.0 - step(radius-width, (proportionX + proportionY));

    // HSB Circle
    vec2 hsbCenter = mouse - uv;
    float hsbAngle = atan(hsbCenter.y, hsbCenter.x);
    float hsbSpeed = 5.0;

    vec3 hsbCircle = createHsbCircle(hsbAngle, hsbSpeed);

    return vec3(circleOutside - circleInside)* hsbCircle;
}

vec3 createHsbCircle(in float c, in float speed) {
    float angle = c + (iTime * speed);
    vec3 hsb = clamp(abs(mod((angle / TWO_PI)*6.0 + vec3(0.0, 4.0, 2.0), 6.0)-3.0)-1.0, 0.0, 1.0);
    hsb = hsb*hsb*(3.0 - 2.0 * hsb);

    return hsb;
}


void mainImage( out vec4 fragColor, in vec2 fragCoord)
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord.xy/iResolution.xy;
    vec2 mouse = iMouse.xy/iResolution.xy;
    float proportion = iResolution.x/iResolution.y;
    float demoSpeed = 2.0;
    vec2 demo = (vec2(sin(iTime*demoSpeed)/proportion, sin(iTime*demoSpeed*2.0)*0.5) + 1.0)/2.0;

    vec2 guide = demo;   

    if (iMouse.z >0.0)
        guide = vec2(mouse);

    vec3 col = circle(uv,proportion, guide);    
    fragColor = vec4(col, 1.0);
}
