void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	// Calculate the aspect ratio
    float aspectRatio = iResolution.y / iResolution.x;

	// Calculate the normalized pixel coordinates
    vec2 uv = ( ( fragCoord.xy / iResolution.xy ) - 0.5 ) * vec2( 1.0, aspectRatio ) + vec2( 0.5 );
    float position = 0.0;


    float t = abs( sin( iTime ) ) / 18.0;

    // distance from the pixel to the center
    position = distance( uv, vec2( 0.5 ) ) * 2.0;
    float circ = smoothstep( 0.29, position, 0.3+t/4.0 );

    vec3 color = vec3( circ );

	fragColor = vec4( color, 1.0 );
}
