void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    //get current color value from input
    vec4 col = texture(iChannel0, uv);

    //make green screen black
    if(col.y > 0.5 && col.x < 0.5 && col.z<0.5){
        col.xyz = vec3(0);
    }
    //if not part of green screen...
    else{
        //weighted method of grayscale conversion
        col.xyz = vec3(0.299*col.x + 0.587*col.y + 0.114*col.z);
        //invert color
        col.xyz = 1. - col.xyz;
        //make more white
        col.xyz *= 1.5;
    }    

    //Output to screen
    fragColor = col;
}
