//checkerboard: https://www.shadertoy.com/view/ssjcRD
//schwarzschild black hole stuff: https://www.desmos.com/calculator/1yft4ghg8h
float checkerboard(vec2 coord, float size){
    vec2 pos = floor(coord/size); 
    return mod(pos.x+pos.y,2.0);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
 vec2 cent = ((-iResolution.xy + 2.0*iMouse.xy)/iResolution.y)*sqrt(8.);
    vec2 uv = 5.*(2.*fragCoord-iResolution.xy)/iResolution.x;
vec2 stuffer=normalize(uv-cent)*1./sqrt((1.-(1./length(uv-cent)))*(1.+(1./length(uv-cent))));
uv=(uv-stuffer);


    // Time varying pixel color
    vec4 col = checkerboard(uv,1.)*vec4(1.);

    // Output to screen
    fragColor = col;
}
