void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = (fragCoord - .5 * iResolution.xy)/iResolution.y;

    float t = iTime;
    uv = fract(sin(uv * t)) - .5;
    vec3 col = vec3(0.05);
    col += smoothstep(0.3,.19, sin(length(uv) * t));
    col.z = sin(t);
    //added due to recommendation by FabriceNeyret
    //AntiAliasing
    float v = sin(length(uv) * t ) - .25;
    col += smoothstep(1.,-1., v/fwidth(v) );
    fragColor = vec4(col,1.0);
}
