void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    #define P 2.0 
    #define Q 1.0
    #define R 1.0
    // 2.0 1.0 1.0 - Julia Sets
    const int MAX_ITER = 2500;
    vec2 c = 2.0 * (iMouse.xy - .5*iResolution.xy) / iResolution.xy;
    vec2 z = 5.0 * (fragCoord - .5*iResolution.xy) / iResolution.xy;
    z.y *= (iResolution.y / iResolution.x);
    for(int i = 0; i < MAX_ITER; i++) {
        z = vec2( Q*z.x*z.x - R*z.y*z.y, P*z.x*z.y ) + c; //z^2+c
        if(distance(z, vec2(0)) > 2.0) {
            float colVal = 50.0*float(i)/float(MAX_ITER)+ 0.2;
            vec3 col = vec3(colVal, 0.1, colVal);
            fragColor = vec4(col, 0.8);
            return;
        }
    }
    fragColor = vec4(0);
}
