void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    //try playing with these
    float outerRadius = 50.0;
    float innerRadius = 50.0;

    vec3 col = vec3(0.0,0.0,0.0);
    //distance of a pixel from the mouse cursor
    float distance = sqrt((fragCoord.y-iMouse.y)*(fragCoord.y-iMouse.y)+(fragCoord.x-iMouse.x)*(fragCoord.x-iMouse.x));

    float innerNormal = 1.0-((distance-1.0)/(innerRadius-1.0));
    float outerNormal = 1.0-((distance-1.0)/(outerRadius-1.0));

    col=vec3(1.0-outerNormal*innerNormal,1.0-outerNormal*innerNormal,1.0-outerNormal*innerNormal);

    fragColor = vec4(col,1.0);
}
