#define PI 3.141592653
#define COUNT 20.0

float drawCoord(vec2 uv)
{
   float x = distance(vec2(uv.x, 0.0), vec2(0.0, 0.0));
   float y = distance(vec2(0.0, uv.y), vec2(0.0, 0.0));

   return 1e-3 / x + 1e-3 / y;
}

float net(vec2 coord)
{
    float xline = cos(coord.x * PI * COUNT);
    xline = step(1.0 - 1e-1, xline);

    float yline = cos(coord.y * PI * COUNT);
    yline = step(1.0 - 1e-1, yline);

    return xline + yline;
}

float sphereLine(vec2 coord, vec2 p, float radius)
{
    float d = distance(p, coord);
    d = abs(d - radius);
    return 1e-3 / d;
}

float sphereShape(vec2 coord, vec2 p, float radius)
{
    float d = distance(p, coord);
    d /= radius;
    d = 1.0 - d;
    d = clamp(d * 24.0, 0.0, 1.0);
    return d;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    float asp = iResolution.x / iResolution.y;

    vec2 coord = uv * 2.0 - 1.0;

    vec2 aspCoord = vec2(coord.x * asp, coord.y);

    vec3 col = vec3(0.0);

    col += net(aspCoord) * 0.125;

    col += drawCoord(aspCoord);

    float sp = sphereShape(aspCoord, vec2(-0.1, 0.5), 0.25);
    sp += sphereShape(aspCoord, vec2(0.1, 0.5), 0.25);

    col += sp;

    sp = sphereShape(aspCoord, vec2(-0.1, -0.5), 0.25);
    sp *= sphereShape(aspCoord, vec2(0.1, -0.5), 0.25);

    col += sp;

    sp = sphereShape(aspCoord, vec2(1.0, 0.5), 0.25);
    sp -= sphereShape(aspCoord, vec2(0.9, 0.5), 0.2);
    col += sp;

    // Output to screen
    fragColor = vec4(col,1.0);
}
