void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float sobelX = 0.0, sobelY = 0.0;

    float Gx[9] = float[9] ( 1.,0.,-1.,2.,0.,-2.,1.,0.,-1. );

    float Gy[9] = float[9] ( 1.,2.,1.,0.,0.,0.,-1.,-2.,-1. );

    for(int y = -1; y < 2; y++)
    {
        int curentY = y + int(fragCoord.y);
        if(curentY >= int(iResolution.y) || curentY < 0)
            continue;
        for(int x = -1; x < 2; x++)
        {
            int curentX = x + int(fragCoord.x);
            if(curentX >= int(iResolution.x) || curentX < 0)
                continue;
            vec2 uv = vec2(curentX, curentY)/iResolution.xy;
            sobelX += length(texture(iChannel0, uv)) * Gx[(x + 1) + ((y + 1) * 3)];
            sobelY += length(texture(iChannel0, uv)) * Gy[(x + 1) + ((y + 1) * 3)];
        }
    }

    float finalColour = length(vec2(sobelX, sobelY));
    if(finalColour >= 1.)
        finalColour = 1.;

    fragColor = vec4(finalColour, finalColour, finalColour, 1.0);
}
