void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord;

    uv -= vec2(0.5, 0.5) * iResolution.xy;

    vec3 col = vec3(1, 1, 1);

    float r = 135.0;
    if(uv.x * uv.x + uv.y * uv.y < r * r){
        float z = sqrt(r * r -uv.x * uv.x - uv.y * uv.y);
        vec3 normal = normalize(vec3(z, uv.y, uv.x));
        vec3 light = normalize(vec3(1.0, 1.0, 1.0));
        float diffuse =clamp(dot(normal, light), 0.0, 1.0);
        float I = diffuse * 0.3 + 0.8;
        col = vec3(I, diffuse, diffuse);

        vec3 d = vec3 (0.1, 0.0, 0.1);
        vec3 reflection = reflect(d, normal);
        I += pow(clamp(dot(reflection,light), 0.1, 1.0), 10.0);
    }


    // Output to screen
    fragColor = vec4(col,1.0);
}
