// antialiasing by FabriceNeyret2
float circle(vec2 uv, float r, float o, vec2 center) {
    float dist = length(uv-center);
    return min(o,1.) * smoothstep(1.5/iResolution.x, 0.0, abs(dist-r) );
}


void mainImage( out vec4 O, in vec2 u )
{
    vec2 uv = ( u - .5*iResolution.xy ) / iResolution.x;

    O *= 0.;
    for(float a, i=0.; i<70.; i++) {
        a = i*(0.4+sin(iTime*0.2));
        vec2 center = vec2( 0.1*sin(iTime+a), 0.1*cos(iTime+a) );
        O += circle(uv, 0.01*i, 0.0+(0.1*i), center);
    }
}
