float Circle(vec2 uv, vec2 pos, float r, float sm){
    uv -= pos;

    return smoothstep(r+sm, r, length(uv));
}
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    float t = iTime;
    uv-=.5;
    uv.x *= iResolution.x/iResolution.y;

    vec3 col = vec3(0.,0.0,0.0);
    float r = .1;
    float sp = 0.05;
    col += Circle(uv, vec2(sp, 0.), abs(cos(t))*r, .01);
    col += Circle(uv, vec2(-sp, 0.), abs(cos(t))*r, .01);
    col += Circle(uv, vec2(.0, sp), abs(sin(t))*r, .01);
    col += Circle(uv, vec2(.0, -sp), abs(sin(t))*r, .01);

    col*=vec3(0.3,0.,0.2);
    if(col == vec3(0.)) col = vec3(abs(sin(t)),abs(sin(t)),abs(cos(t)));
    // Time varying pixel color
    //col += 0.5 + 0.5*cos(iTime+uv.xyx+vec3(0,2,4));

    // Output to screen
    fragColor = vec4(col,1.0);
}
