/*
by Dom Mandy in 2023
*/
void mainImage(out vec4 o, vec2 i) {
    // +33 = 127 characters with the animation
    i = mod(iTime / 1e3 + i, iResolution.x);

    // 94 characters for static
    ivec2 m = floatBitsToInt(i) / 99 * 99;
    o = vec4(0);
    o[(m.x ^ m.y)%7]++;
}


